import React, { useState, useEffect } from "react";
import "./style.css";
import { Link } from "react-router-dom";
import axios from 'axios';


const Home = () => {
  const [houses, setHouses] = useState([]);
  const [filteredHouses, setFilteredHouses] = useState([]);
  const [design, setDesign] = useState("");
  const [price, setPrice] = useState("");
  const [area, setArea] = useState("");

  useEffect(() => {
    fetch("/api/houses")
      .then((response) => response.json())
      .then((data) => {
        if (Array.isArray(data)) {
          setHouses(data);
          setFilteredHouses(data); // Initialize with all houses
        } else {
          console.error("Unexpected data format:", data);
        }
      })
      .catch((error) => console.error("Fetch error:", error));
  }, []);

  const [query, setQuery] = useState('');
    const [results, setResults] = useState([]);

    const handleSearch = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.get(`/search`, { params: { query } });
            setResults(response.data);  // Update state with the search results
        } catch (error) {
            console.error('Error during search:', error);
        }
    };

  const filterHouses = () => {
    console.log("Price filter state:", price); // Check if price filter is passed correctly

    const filtered = houses.filter((house) => {
      // Extract numeric value from the price string, e.g., "₹ 20 lakhs" becomes 20 * 100000 (convert lakhs to rupees)
      const housePrice = house.price
        ? parseInt(house.price.replace(/[^\d]/g, "")) * 100000
        : null;

      // Extract numeric value from area string, e.g., "1100 sq ft" becomes 1100
      const houseArea = house.area
        ? parseInt(house.area.replace(/[^\d]/g, ""))
        : null;

      // Define min and max price based on selected filter (e.g., "15-20 lakhs")
      let minPrice = null;
      let maxPrice = null;
      if (price && price !== "") {
        const priceRange = price.split("-"); // Split the "15-20 lakhs" format into ["15", "20"]
        console.log("Price range:", priceRange); // Check if the split works correctly

        // Check if the priceRange is an array with two values
        if (priceRange.length === 2) {
          minPrice = parseInt(priceRange[0].replace(/[^\d]/g, "")) * 100000; // converting lakhs to rupees
          maxPrice = parseInt(priceRange[1].replace(/[^\d]/g, "")) * 100000;
        }
      }

      // Log the parsed price for debugging
      console.log("House Price:", housePrice);
      console.log("Min Price:", minPrice, "Max Price:", maxPrice);

      // Define min and max area based on selected filter (e.g., "300-500 sq/ft")
      let minArea = null;
      let maxArea = null;
      if (area && area !== "") {
        const areaRange = area.split("-");
        if (areaRange.length === 2) {
          minArea = parseInt(areaRange[0].replace(/[^\d]/g, ""));
          maxArea = parseInt(areaRange[1].replace(/[^\d]/g, ""));
        }
      }

      // Apply the filters: design, price, and area
      return (
        (design === "" || house.name === design) &&
        (price === "" ||
          (housePrice && housePrice >= minPrice && housePrice <= maxPrice)) &&
        (area === "" ||
          (houseArea && houseArea >= minArea && houseArea <= maxArea))
      );
    });

    setFilteredHouses(filtered);
  };

  const renderHouseList = () => {
    if (!filteredHouses || filteredHouses.length === 0) {
      return <p>No houses available.</p>;
    }

    return filteredHouses.map((house, index) => (
      <div key={index} className="col-sm-4 col-md-5 col-lg-3 col-xl-3 card">
        <Link
          to={`/houseDetails/${house._id}`}
          className="text-reset text-decoration-none"
          target="_blank"
        >
          <img src={house.img} className="card-img-top" alt={house.name} />
          <div className="card-body">
            <div className="card-content">
              <h5>{house.name}</h5>
              <p>{house.desp}</p>
            </div>
            <div className="card-btn text-end">
              <button className="btn btn-outline-dark">Visit</button>
            </div>
          </div>
        </Link>
      </div>
    ));
  };

  return (
    <main className="col-sm-12 col-md-12 col-lg-12 col-xl-12 main p-5">
      <div className="col-sm-12 col-md-12 col-lg-12 col-xl-12 text-center title-text">
        A complete solution for Planning & Designing your dream Home
      </div>
      <div className="container input-group search-input mt-4 mb-5">
        <input
          type="text"
          className="form-control border border-dark"
          placeholder="Enter your requirements..."
          aria-label="Enter your requirements..."
          aria-describedby="button-addon2"
        />
        <button
          className="btn bg-light input-group-append"
          type="button"
          id="button-addon2"
        >
          <i className="fa fa-search"></i>
        </button>
      </div>
      <div className="col-sm-12 col-md-12 col-lg-12 col-xl-12 px-4">
        <div className="row justify-content-center col-sm-12 col-md-12 col-lg-12 col-xl-12">
          <span className="col-sm-12 col-md-12 col-lg-12 col-xl-12 findby text-center">
            Find by
          </span>
          <span className="col-sm-2 col-md-2 col-lg-2 col-xl-2">
            <select
              id="designs"
              className="form-select find-select"
              aria-label="Default select example"
              onChange={(e) => setDesign(e.target.value)}
            >
              <option value="">Designs</option>
              <option value="Bungalow">Bungalow</option>
              <option value="Villa">Villa</option>
              <option value="Building">Buildings</option>
              <option value="Complex">Complex</option>
              <option value="Penthouse">Penthouse</option>
              <option value="Farmhouses">Farmhouses</option>
              <option value="Cabin">Cabin</option>
            </select>
          </span>
          <span className="col-sm-2 col-md-2 col-lg-2 col-xl-2">
            <select
              id="price"
              className="form-select find-select"
              aria-label="Default select example"
              onChange={(e) => setPrice(e.target.value)}
            >
              <option value="">Price Range</option>
              <option value="10-20 lakhs">10-20 lakhs</option>
              <option value="20-30 lakhs">20-30 lakhs</option>
              <option value="30-40 lakhs">30-40 lakhs</option>
              <option value="40-50 lakhs">40-50 lakhs</option>
              <option value="50-70 lakhs">50-70 lakhs</option>
              <option value="70-100 lakhs">70-100 lakhs</option>
              <option value="100-200 lakhs">Above 1 Cr</option> 
            </select>
          </span>
          <span className="col-sm-2 col-md-2 col-lg-2 col-xl-2">
            <select
              id="area"
              className="form-select find-select"
              aria-label="Default select example"
              onChange={(e) => setArea(e.target.value)}
            >
              <option value="">Area Sq/Ft</option>
              <option value="300-500 sq/ft">300-500 sq/ft</option>
              <option value="1000-2000 sq/ft">1000-2000 sq/ft</option>
              <option value="1500-2500 sq/ft">1500-2500 sq/ft</option>
              <option value="2000-3000 sq/ft">2000-3000 sq/ft</option>
            </select>
          </span>
          <button
            id="findButton"
            className="col-sm-1 col-md-1 col-lg-1 col-xl-1 btn btn-outline-light find-btn"
            onClick={filterHouses}
          >
            Find
          </button>
        </div>
      </div>

      <div className="container col-sm-12 col-md-12 col-lg-12 col-xl-12">
        <h3 className="pt-5">Popular Designs</h3>
        <div
          className="col-sm-12 col-md-12 col-lg-12 col-xl-12 row gx-0 gap-5 mt-3 mx-auto justify-content-center"
          id="houseList"
        >
          {renderHouseList()}
        </div>
      </div>
